mapper.update:
UPDATE cart
SET amount = #{amount}
WHERE m_idx = #{m_idx} AND p_id = #{p_id}

mapper.insert:
	    
	    insert into cart (m_idx, p_id, amount)
        values (#{m_idx}, #{p_id}, #{amount})
        on duplicate key update amount = amount + #{amount}

mapper.update:
	    UPDATE cart
	    SET amount = amount + 1
        WHERE m_idx = #{m_idx} AND p_id = #{p_id}

mapper.delete:
	DELETE
	FROM cart
	WHERE m_idx IN
    
        #{item.m_idx}
    
    AND p_id IN
    
        #{item.p_id}

