mapper.update:
UPDATE cart
SET amount = #{amount}
WHERE m_idx = #{m_idx} AND p_id = #{p_id}

mapper.delete:
DELETE
FROM cart
WHERE m_idx = #{m_idx} AND p_id = #{p_id}

